mapper.update:
UPDATE TB_TEAM_MEMBER
SET delete_yn = 'Y'
WHERE user_no = #{userNo}

UPDATE TB_TEAM_MEMBER
SET delete_yn = 'Y'
WHERE user_no = #{userNo} and team_no = #{teamNo}

mapper.select:
SELECT team_name, create_date 
FROM TB_TEAM
WHERE team_no = #{teamNo}

mapper.update:
UPDATE TB_INVITED
SET group_no = 'F002'
WHERE invited_id = #{invitedId}

UPDATE TB_INVITED
SET group_no = 'F003'
WHERE invited_id = #{invitedId}
